import React, { useEffect, useState } from 'react';

const styles = {
  container: {
    fontFamily: 'Arial, sans-serif',
    maxWidth: '600px',
    margin: '0 auto',
    padding: '20px',
  },
  header: {
    fontSize: '24px',
    marginBottom: '10px',
  },
  list: {
    marginBottom: '20px',
    padding: '0',
    listStyle: 'none',
  },
  listItem: {
    marginBottom: '5px',
    padding: '5px',
    borderRadius: '5px',
    backgroundColor: '#f2f2f2',
  },
  title: {
    fontSize: '18px',
    fontWeight: 'bold',
    marginBottom: '5px',
  },
  score: {
    fontSize: '14px',
    color: '#666666',
  },
};

const AuthorList = () => {
  const [hubAuthors, setHubAuthors] = useState([]);
  const [authorityAuthors, setAuthorityAuthors] = useState([]);

  useEffect(() => {
    fetchAuthors();
  }, []);

  const fetchAuthors = async () => {
    try {
      const response = await fetch('/api/authors'); // Replace with your backend API endpoint
      const data = await response.json();

      const sortedHubAuthors = [...data].sort((a, b) => b.hubScore - a.hubScore);
      const sortedAuthorityAuthors = [...data].sort(
        (a, b) => b.authorityScore - a.authorityScore
      );

      setHubAuthors(sortedHubAuthors);
      setAuthorityAuthors(sortedAuthorityAuthors);
    } catch (error) {
      console.error('Error fetching authors:', error);
    }
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.header}>Authors with Best Hub Score</h2>
      {hubAuthors.length > 0 ? (
        <ul style={styles.list}>
          {hubAuthors.map(author => (
            <li key={author.id} style={styles.listItem}>
              <div style={styles.title}>{author.name}</div>
              <div style={styles.score}>Hub Score: {author.hubScore}</div>
            </li>
          ))}
        </ul>
      ) : (
        <p>Loading authors...</p>
      )}

      <h2 style={styles.header}>Authors with Best Authority Score</h2>
      {authorityAuthors.length > 0 ? (
        <ul style={styles.list}>
          {authorityAuthors.map(author => (
            <li key={author.id} style={styles.listItem}>
              <div style={styles.title}>{author.name}</div>
              <div style={styles.score}>Authority Score: {author.authorityScore}</div>
            </li>
          ))}
        </ul>
      ) : (
        <p>Loading authors...</p>
      )}
    </div>
  );
};

export default AuthorList;
